import React, { Component } from "react";

class Counter extends Component {
	render() {
		console.log("props", this.props);
		if (this.props.counter.value < 0) {
			return (
				<div>
					<p>Product has no valid quantity! Please add at least one product</p>
					<button
						onClick={() => this.handleIncrement()}
						className='btn btn-secondary m-2 btn-sm'>
						+
					</button>
				</div>
			);
		}

		return (
			<div>
				<h4>Counter #{this.props.counter.id}</h4>
				<button
					onClick={() => this.props.onDecrement(this.props.counter)}
					className='btn btn-secondary m-2 btn-sm'>
					-
				</button>
				<button
					onClick={() => this.props.onIncrement(this.props.counter)}
					className='btn btn-secondary m-2 btn-sm'>
					+
				</button>
				<span className={this.renderBadgeClasses()}>
					{this.formatCounterText()}
				</span>
				<button
					className='btn-danger btn-sm m-2'
					onClick={() => this.props.onDelete(this.props.counter.id)}>
					Delete
				</button>
			</div>
		);
	}

	renderBadgeClasses() {
		let badgeColor = "badge m-2 badge-";
		badgeColor += this.props.counter.value === 0 ? "warning" : "primary";

		return badgeColor;
	}

	formatCounterText() {
		const { value } = this.props.counter;
		return value === 0 ? "zero" : value;
	}
}

export default Counter;
