import React, { Component } from "react";

class Counter extends Component {
	state = {
		count: 0
	};
	handleIncrement = (product) => {
		console.log("product", product);
		this.setState({ count: (this.state.count += 1) });
		console.log(this.state.count);
		console.log("increment btn is clicked");
	};

	render() {
		return (
			<div>
				<span className={this.renderBadgeClasses()}>{this.formatCount()}</span>
				<button
					onClick={() => this.handleIncrement(this.product)}
					className='btn btn-secondary btn-sm'>
					Increment
				</button>
			</div>
		);
	}

	renderBadgeClasses() {
		let badgeColor = "badge m-2 badge-";
		badgeColor += this.state.count === 0 ? "warning" : "primary";

		return badgeColor;
	}

	formatCount() {
		const { count } = this.state;
		return count === 0 ? "zero" : count;
	}
}

export default Counter;
