import React, { Component } from "react";

import Counter from "./counter.jsx";

class Counters extends Component {
	state = {
		counters: [
			{ id: 1, value: 0 },
			{ id: 2, value: 0 },
			{ id: 3, value: 0 },
			{ id: 4, value: 0 }
		]
	};

	handleDelete = (counterId) => {
		console.log("response to event handleDelete called", counterId);
		const countersAfterDelete = this.state.counters.filter(
			(e) => e.id !== counterId
		);
		this.setState({ counters: countersAfterDelete });
	};
	handleReset = () => {
		const countersToReset = this.state.counters.map((c) => {
			c.value = 0;
			return c;
		});
		this.setState({ counters: countersToReset });
	};
	handleIncrement = (counter) => {
		const counters = [...this.state.counters];
		const index = counters.indexOf(counter);
		counters[index] = { ...counter };
		const updateCounter = counters[index].value++;
		this.setState({ counters: updateCounter });
	};

	render() {
		return (
			<div>
				<button className='btn-primary btn-sm m-2' onClick={this.handleReset}>
					Reset button
				</button>
				{this.state.counters.map((counter) => (
					<Counter
						key={counter.id}
						counter={counter}
						onDelete={this.handleDelete}
						onIncrement={this.handleIncrement}
						onDecrement={this.handleDecrement}
					/>
				))}
			</div>
		);
	}
}

export default Counters;
