import React, { Component } from "react";

class Counter extends Component {
	state = {
		count: 0,
		tags: [
			{ content: "tag1", id: 1 },
			{ content: "tag2", id: 2 },
			{ content: "tag3", id: 3 }
		]
	};

	constructor() {}

	handleIncrement() {
		console.log(this);
		console.log("increment btn is clicked");
	}

	render() {
		return (
			<div>
				<span className={this.renderBadgeClasses()}>{this.formatCount()}</span>
				<button
					onClick={this.handleIncrement}
					className='btn btn-secondary btn-sm'>
					Increment
				</button>
				<ul>
					{this.state.tags.map((objItem) => (
						<li key={objItem.id}>{objItem.content}</li>
					))}
				</ul>
			</div>
		);
	}

	renderBadgeClasses() {
		let badgeColor = "badge m-2 badge-";
		badgeColor += this.state.count === 0 ? "warning" : "primary";

		return badgeColor;
	}

	formatCount() {
		const { count } = this.state;
		return count === 0 ? "zero" : count;
	}
}

export default Counter;
