import React, { Component } from "react";

class Counter extends Component {
	state = {
		value: this.props.value
	};
	/*handleIncrement = (product) => {
		//console.log("product +", product);
		this.setState({ value: (this.state.value += 1) });
		console.log(this.state.value);
		console.log("increment btn is clicked");
	};
	handleDecrement = (product) => {
		console.log("product -", product);
		this.setState({ value: (this.state.value -= 1) });
		console.log(this.state.value);
		console.log("decrement btn is clicked");
	};*/

	render() {
		console.log("props", this.props);
		if (this.state.value < 0) {
			return (
				<div>
					<p>Product has no valid quantity! Please add at least one product</p>
					<button
						onClick={() => this.handleIncrement(this.product)}
						className='btn btn-secondary btn-sm'>
						+
					</button>
				</div>
			);
		}
		return (
			<div>
				<h4>Counter #{this.props.id}</h4>
				<button
					onClick={() => this.handleDecrement(this.product)}
					className='btn btn-secondary m-2 btn-sm'>
					-
				</button>
				<span className={this.renderBadgeClasses()}>
					{this.formatCounterText()}
				</span>
				<button
					onClick={() => this.handleIncrement(this.product)}
					className='btn btn-secondary btn-sm'>
					+
				</button>
				<button
					className='btn-danger btn-sm m-2'
					onClick={() => this.props.onDelete(this.props)}>
					Delete
				</button>
			</div>
		);
	}

	renderBadgeClasses() {
		let badgeColor = "badge m-2 badge-";
		badgeColor += this.state.value === 0 ? "warning" : "primary";

		return badgeColor;
	}

	formatCounterText() {
		const { value } = this.state;
		return value === 0 ? "zero" : value;
	}
}

export default Counter;
