import React, { Component } from "react";

class Counter extends Component {
	//()
	componentDidUpdate(prevProps, prevState) {
		console.log("prevProps", prevProps);
		console.log("prevState", prevState);

		if (prevProps.counters.value !== this.props.counters.value) {
			console.log("value of a counter is changed!");
		}
	}

	render() {
		console.log("App - Counter");
		if (this.props.counters.value < 0) {
			return (
				<div>
					<p>Product has no valid quantity! Please add at least one product</p>
					<button
						onClick={() => this.props.onIncrement(this.props.counters)}
						className='btn btn-secondary m-2 btn-sm'>
						+
					</button>
				</div>
			);
		}
		return (
			<div>
				<button
					onClick={() => this.props.onDecrement(this.props.counters)}
					className='btn btn-secondary m-2 btn-sm'>
					-
				</button>
				<button
					onClick={() => this.props.onIncrement(this.props.counters)}
					className='btn btn-secondary m-2 btn-sm'>
					+
				</button>
				<span className={this.renderBadgeClasses()}>
					{this.formatCounterText()}
				</span>
				<button
					className='btn-danger btn-sm m-2'
					onClick={() => this.props.onDelete(this.props.counters.id)}>
					Delete
				</button>
			</div>
		);
	}

	renderBadgeClasses() {
		let badgeColor = "badge m-2 badge-";
		badgeColor += this.props.counters.value === 0 ? "warning" : "primary";

		return badgeColor;
	}

	formatCounterText() {
		const { value } = this.props.counters;
		return value === 0 ? "zero" : value;
	}
}

export default Counter;
