import React, { Component } from "react";

class Counter extends Component {
	render() {
		const { counters } = this.props;
		if (counters.value < 0) {
			return (
				<div>
					<p>Product has no valid quantity! Please add at least one product</p>
					<button
						onClick={() => this.props.onIncrement(counters)}
						className='btn btn-secondary m-2 btn-sm'>
						+
					</button>
				</div>
			);
		}

		return (
			<div>
				<button
					onClick={() => this.props.onDecrement(counters)}
					className='btn btn-secondary m-2 btn-sm'>
					-
				</button>
				<button
					onClick={() => this.props.onIncrement(counters)}
					className='btn btn-secondary m-2 btn-sm'>
					+
				</button>
				<span className={this.renderBadgeClasses()}>
					{this.formatCounterText()}
				</span>
				<button
					className='btn-danger btn-sm m-2'
					onClick={() => this.props.onDelete(counters.id)}>
					Delete
				</button>
			</div>
		);
	}

	renderBadgeClasses() {
		let badgeColor = "badge m-2 badge-";
		badgeColor += counters.value === 0 ? "warning" : "primary";

		return badgeColor;
	}

	formatCounterText() {
		const { value } = counters;
		return value === 0 ? "zero" : value;
	}
}

export default Counter;
